import HeaderA from "./HeaderA.js";
import "../../css/Plantilla.css";
import "../../css/admi.css"

function Admi() {
    const openForm = (numBtn) => {
        switch (numBtn) {
            case 1:
                document.getElementById("camiloUser").style.display = "flex";
                document.getElementById("andresClients").style.display = "none";
                document.getElementById("jaimeProveedores").style.display = "none";
                document.getElementById("davidProductos").style.display = "none";
                break;
            case 2:
                document.getElementById("camiloUser").style.display = "none";
                document.getElementById("andresClients").style.display = "flex";
                document.getElementById("jaimeProveedores").style.display = "none";
                document.getElementById("davidProductos").style.display = "none";
                break;
            case 3:
                document.getElementById("camiloUser").style.display = "none";
                document.getElementById("andresClients").style.display = "none";
                document.getElementById("jaimeProveedores").style.display = "flex";
                document.getElementById("davidProductos").style.display = "none";
                break;
            case 4:
                document.getElementById("camiloUser").style.display = "none";
                document.getElementById("andresClients").style.display = "none";
                document.getElementById("jaimeProveedores").style.display = "none";
                document.getElementById("davidProductos").style.display = "flex";
                break;

            default:
                break;
        };

    };
    return (
        <div className="containerDad">
            <HeaderA />
            <article className="containerInterfazeAdmi">
                {/* contenedor Aside lado izquierdo */}
                <aside >
                    <section className="nombreUsuario" >
                        <h2 id="user">Administrador</h2>
                    </section>
                    <section>
                        <button onClick={() => openForm(1)} className="btn-izq">
                            Usuarios
                        </button>
                    </section>
                    <section>
                        <button onClick={() => openForm(2)} className="btn-izq">
                            Clientes
                        </button>
                    </section>
                    <section>
                        <button onClick={() => openForm(3)} className="btn-izq">
                            Proveedores
                        </button>
                    </section>
                    <section>
                        <button onClick={() => openForm(4)} className="btn-izq">
                            Productos
                        </button>
                    </section>
                </aside>
                {/* contenedor Aside lado izquierdo */}
                <section className="containerForms" >
                    {/*a qui cada uno importara la interfaz que le corresponde */}
                    <div id="camiloUser" className="boxForm"></div>
                    <div id="andresClients" className="boxForm"></div>
                    <div id="jaimeProveedores" className="boxForm"></div>
                    <div id="davidProductos" className="boxForm"></div>
                </section>
            </article>
        </div>
    )
}

export default Admi;
