import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export function useAuth() {
    return useContext(AuthContext);
}

export const AuthProvider = ({ children }) => {
    const [autenticado, setAutenticado] = useState(() => {
        // Revisa en el almacenamiento local si el usuario ya está autenticado
        const isAuth = localStorage.getItem('isAuth');
        return isAuth === 'true'; // Asegúrate de comparar con el string 'true'
    });

    const login = () => {
        localStorage.setItem('isAuth', 'true'); // Guarda el estado de autenticación en el almacenamiento local
        setAutenticado(true);
    };

    const logout = () => {
        localStorage.removeItem('isAuth'); // Elimina el estado de autenticación del almacenamiento local
        setAutenticado(false);
    };

/*     // Efecto para limpiar LocalStorage cuando el componente se desmonta
    useEffect(() => {
        return () => {
            localStorage.removeItem('isAuth');
        };
    }, []); */

    return (
        <AuthContext.Provider value={{ autenticado, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
