import HeaderA from "./HeaderA.js";
import "../../css/Plantilla.css";
import "../../css/admi.css";

function Admi() {
    const formIds = {
        1: "camiloUser",
        2: "andresClients",
        3: "jaimeProveedores",
        4: "davidProductos",
    };

    const openForm = (numBtn) => {
        // Esconder todos los formularios primero
        Object.values(formIds).forEach(id => {
            document.getElementById(id).style.display = "none";
        });

        // Mostrar el formulario seleccionado
        const selectedFormId = formIds[numBtn];
        if (selectedFormId) {
            document.getElementById(selectedFormId).style.display = "flex";
        }
    };

    return (
        <div className="containerDad">
            <HeaderA />
            <article className="containerInterfazeAdmi">
                <aside>
                    <section className="nombreUsuario">
                        <h2 id="user">Administrador</h2>
                    </section>
                    {Object.entries(formIds).map(([num, id]) => (
                        <section key={id}>
                            <button onClick={() => openForm(num)} className="btn-izq">
                                {id.split(/(?=[A-Z])/).join(" ")} {/* Esto convierte "camiloUser" en "Camilo User" */}
                            </button>
                        </section>
                    ))}
                </aside>
                <section className="containerForms">
                    <div id="camiloUser" className="boxForm"></div>
                    <div id="andresClients" className="boxForm"></div>
                    <div id="jaimeProveedores" className="boxForm"></div>
                    <div id="davidProductos" className="boxForm"></div>
                </section>
            </article>
        </div>
    );
}

export default Admi;
