import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export function useAuth() {
    return useContext(AuthContext);
}

export const AuthProvider = ({ children }) => {
    const [autenticado, setAutenticado] = useState(false);
    const [usuario, setUsuario] = useState(null); // Nuevo estado para guardar la información del usuario

    const login = (userInfo) => {
        localStorage.setItem('isAuth', 'true');
        localStorage.setItem('userInfo', JSON.stringify(userInfo)); // Guarda la información del usuario en localStorage
        setAutenticado(true);
        setUsuario(userInfo); // Actualiza el estado con la información del usuario
    };

    const logout = () => {
        localStorage.removeItem('isAuth');
        localStorage.removeItem('userInfo'); // Limpia la información del usuario de localStorage
        setAutenticado(false);
        setUsuario(null); // Limpia el estado del usuario
    };

    useEffect(() => {
        const isAuth = localStorage.getItem('isAuth') === 'true';
        const userInfo = localStorage.getItem('userInfo') ? JSON.parse(localStorage.getItem('userInfo')) : null;
        setAutenticado(isAuth);
        setUsuario(userInfo); // Recupera la información del usuario al recargar
    }, []);

    return (
        <AuthContext.Provider value={{ autenticado, usuario, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
